import { Card, Popover } from "antd";
import {
  RetweetOutlined,
  HeartOutlined,
  MessageOutlined,
  EllipsisOutlined,
} from "@ant-design/icons";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages images={post.Images} />}
        actions={[
          <RetweetOutlined key="retweet" />,
          <HeartOutlined key="heart" />,
          <MessageOutlined key="connent" />,
          <Popover key="more">
            <EllipsisOutlined />
          </Popover>,
        ]}
      ></Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
